    private void excluirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_excluirActionPerformed
       int id = this.tabelinha.getSelectedRow();
       this.controle.deletar(id); 
       this.carregarTabela(this.controle.getListaAnimais()); 
       JOptionPane.showMessageDialog(null, "Animal excluido com sucesso");
    }//GEN-LAST:event_excluirActionPerformed



    
    private void salvarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_salvarActionPerformed
       Animal animal = new Animal(this.txtCAF.getText(), this.txtTipo.getText(),this.txtNome.getText(),this.txtPeso.getText());


           if(this.controle.salvar(animal)){
               this.limpar();
               this.carregarTabela(this.controle.getListaAnimais());
               JOptionPane.showMessageDialog(null, "Seu pet foi adicionado com sucesso!"); 
           }
    }//GEN-LAST:event_salvarActionPerformed

