    private void editarMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_editarMouseClicked
         int id = this.tabelinha.getSelectedRow();
         this.controle.deletar(id); 
         
          if (id == -1) {
        JOptionPane.showMessageDialog(this, "Selecione uma linha para editar");
          }
        
    String CAF = tabelinha.getValueAt(id, 0).toString(); 
    String Tipo = tabelinha.getValueAt(id, 2).toString(); 
    String Nome = tabelinha.getValueAt(id, 1).toString(); 
    String Peso = tabelinha.getValueAt(id, 3).toString(); 


    txtCAF.setText(CAF);
    txtTipo.setText(Tipo);
     txtNome.setText(Nome);
      txtPeso.setText(Peso);
    }//GEN-LAST:event_editarMouseClicked

